import React from 'react';

function MicOptions({ setVoiceInput, setVoiceOutput, setShowMicOptions }) {
  const disableMic = () => {
    setVoiceInput(false);
    setVoiceOutput(false);
    alert("üé§ Mic Disabled");
    setShowMicOptions(false);
  };

  return (
    <div id="micOptions" style={{
      position: "absolute", bottom: 80, right: 20,
      background: "#1e293b", padding: 10, borderRadius: 10,
      boxShadow: "0 2px 8px rgba(0,0,0,0.5)", zIndex: 99
    }}>
      <button onClick={() => {
        setVoiceInput(true);
        setShowMicOptions(false);
        const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
        recognition.lang = 'en-US';
        recognition.start();
        recognition.onresult = function (event) {
          const transcript = event.results[0][0].transcript;
          document.querySelector('input[type="text"]').value = transcript;
        };
      }}>üéô Take Input</button>
      <button onClick={() => {
        setVoiceOutput(true);
        alert("üîä Voice output enabled");
        setShowMicOptions(false);
      }}>üîä Read Response</button>
      <button onClick={disableMic}>‚ùå Disable Mic</button>
    </div>
  );
}

export default MicOptions;
