// src/components/Chat.jsx
import React, { useState } from 'react';

function Chat({
  chat, onSend, status,
  showMicOptions, setShowMicOptions,
  voiceInput, setVoiceInput, voiceOutput, setVoiceOutput,
  onToggleSidebar
}) {
  const [input, setInput] = useState('');

  const handleVoiceInput = () => {
    setVoiceInput(true);
    const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
    recognition.lang = 'en-US';
    recognition.start();
    recognition.onresult = function (event) {
      const transcript = event.results[0][0].transcript;
      setInput(transcript);
      onSend(transcript);
    };
    recognition.onerror = function (event) {
      alert("Voice input error: " + event.error);
    };
    setShowMicOptions(false);
  };

  const handleSend = () => {
    if (input.trim()) {
      onSend(input.trim());
      setInput('');
    }
  };

  const regenerate =()=>{
    
  }

  return (
    <div className="main">
      <button className="sidebar-toggle" onClick={onToggleSidebar}>â˜°</button>
      <div className="chat-container">
        {chat?.messages.map((m, i) => (
          <div key={i} className={`message ${m.type}`}>{m.text}</div>
        ))}
      </div>
      <div id="status">{status}</div>
      <div className="chat-input-container">
        <input
          type="text"
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && handleSend()}
          placeholder="Type your message..."
        />
        <button onClick={handleVoiceInput}>ðŸŽ¤</button>
        <button onClick={handleSend}>âž¤</button>
        <button onClick={() => window.speechSynthesis.cancel()}>ðŸ›‘</button>
      </div>
    </div>
  );
}

export default Chat;
