// src/components/MicOptions.jsx
import React from 'react';

let recognition;

function MicOptions({ setVoiceInput, setVoiceOutput, setShowMicOptions }) {
  const handleVoiceInput = () => {
    if (!('webkitSpeechRecognition' in window || 'SpeechRecognition' in window)) {
      alert("‚ùå Voice input not supported in this browser.");
      return;
    }

    setVoiceInput(true);
    setShowMicOptions(false);

    recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
    recognition.lang = 'en-US';
    recognition.start();

    recognition.onresult = function (event) {
      const transcript = event.results[0][0].transcript;
      const inputBox = document.querySelector('input[type="text"]');
      if (inputBox) inputBox.value = transcript;
    };

    recognition.onerror = function (event) {
      alert("üé§ Voice input error: " + event.error);
    };
  };

  const handleVoiceOutput = () => {
    setVoiceOutput(true);
    alert("üîä Voice output enabled");
    setShowMicOptions(false);
  };

  const disableMic = () => {
    setVoiceInput(false);
    setVoiceOutput(false);
    setShowMicOptions(false);

    // Stop voice input
    try {
      if (recognition) recognition.abort();
    } catch (e) {}

    // Stop voice output
    try {
      window.speechSynthesis.cancel();
    } catch (e) {}

    alert("‚ùå Mic disabled");
  };

  return (
    <div id="micOptions">
      <button onClick={handleVoiceInput}>üéô Take Voice Input</button>
      <button onClick={handleVoiceOutput}>üîä Read Response</button>
      <button onClick={disableMic}>‚ùå Disable Mic</button>
    </div>
  );
}

export default MicOptions;
