import React, { useState } from 'react';

function Chat({
  chat, onSend, status,
  showMicOptions, setShowMicOptions,
  voiceInput, setVoiceInput, voiceOutput, setVoiceOutput,
  onToggleSidebar
}) {
  const [input, setInput] = useState('');

  const handleSend = () => {
    if (input.trim()) {
      onSend(input.trim());
      setInput('');
    }
  };

  return (
    <div className="main">
      <button className="sidebar-toggle" onClick={onToggleSidebar}>â˜°</button>
      <div className="chat-container">
        {chat?.messages.map((m, i) => (
          <div key={i} className={`message ${m.type}`}>{m.text}</div>
        ))}
      </div>
      <div id="status">{status}</div>
      <div className="chat-input-container">
        <input
          type="text"
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && handleSend()}
          placeholder="e.g., Perform linear search on [4, 8, 15] to find 15"
        />
        <button onClick={() => setShowMicOptions(true)}>ðŸŽ¤</button>
        <button onClick={handleSend}>âž¤</button>
        <button onClick={() => window.speechSynthesis.cancel()}>ðŸ›‘</button>
      </div>
    </div>
  );
}

export default Chat;
